"use strict";new Swiper(".mySwiper",{navigation:{nextEl:".swiper-button-next",prevEl:".swiper-button-prev"},loop:!0,slidesPerView:1});const hamb=document.querySelector(".hamb__field"),menu=document.querySelector(".header__mobile-wrapper"),body=document.body,menuLink=document.querySelector(".header__mobile-nav");function hambHandler(e){e.preventDefault(),hamb.classList.toggle("hamb__field-active"),menu.classList.toggle("active"),body.classList.toggle("noscroll"),renderPopup()}function renderPopup(){menu.appendChild(menuLink)}hamb.addEventListener("click",hambHandler);const links=Array.from(menu.children);function closeOnClick(){hamb.classList.toggle("hamb__field-active"),menu.classList.toggle("active"),body.classList.toggle("noscroll")}links.forEach(e=>{e.addEventListener("click",closeOnClick)});const btn=document.querySelector(".btn-show"),cards=Array.from(document.querySelectorAll(".card"));function openCaralog(){btn.addEventListener("click",()=>{cards.forEach(e=>e.classList.remove("hidden"))})}function response1(){1200<window.innerWidth&&cards.forEach((e,n)=>{e.classList.add("hidden"),n<=3?e.classList.remove("hidden"):3<n&&btn.classList.remove("hidden"),openCaralog()})}function response2(){window.innerWidth<=1200&&cards.forEach((e,n)=>{e.classList.add("hidden"),n<=2?e.classList.remove("hidden"):2<n&&btn.classList.remove("hidden"),openCaralog()})}window.addEventListener("resize",e=>{768<e.target.window.innerWidth&&response1(),e.target.window.innerWidth<768&&response2()}),response1(),response2();